function initPage(){
  width = svg.clientWidth;
  height = svg.clientHeight;
  if(width > 768){
    r = Math.floor(width/18);
    min_x = 0.5;
    min_y = 2;
    max_x = 5.5;
    max_y = 7;
    if(width > 1240){
      min_x = 0.5;
      min_y = 1;
      max_x = 5.5;
      max_y = 5;
    }
  }else{
    r = Math.floor(width/9);
    min_x = 0;
    min_y = 1;
    max_x = 3;
    max_y = 6;
  }
  h = (Math.sin(Math.PI/3)*r);

  function getBackLine(){
    var g = document.createElementNS("http://www.w3.org/2000/svg","g");
    for(var i=0; i<8; i++){
      for(var j=0; j<16; j++){
        g.appendChild(drawPath((r - h + (3*r*i)),(2*h*j),'none', i,j));
        g.appendChild(drawPath((5*r/2 - h + (3*r*i)),(h + (2*h*j)),'none',i+0.5,j+0.5));
      }
    }
    drawIcon(g);
    return g;
  }
  g6.innerHTML = '';
  g6.appendChild(getBackLine());

  function drawPath(x,y,color,i,j,p){
    var g = document.createElementNS("http://www.w3.org/2000/svg","g");
    var path = document.createElementNS("http://www.w3.org/2000/svg","path");
    var d = 'M' + (-r) + ',0l' + (r/2) + ',' + (-h) + 'h' + r + 'L' + r + ',0l' + (-r/2) + ',' + h + 'h' + (-r) + 'L' + (-r) + ',0z';
    g.setAttribute('transform','translate(' + x + ',' + y + ')');
    path.setAttribute('d',d);
    path.setAttribute('stroke-width',1);
    path.setAttribute('stroke','#333');
    path.setAttribute('fill',color);
    path.setAttribute('opacity',((Math.random()+0.4)/2));
    g.appendChild(path);
    g.setAttribute('data-x',i);
    g.setAttribute('data-y',j);
    return g;
  }
  function drawIcon(group){
    var paths = group.getElementsByTagName('g');
    var len = paths.length;
    var arr = [];
    for(var i=0; i<len; i++){
      var x = paths[i].getAttribute('data-x');
      var y = paths[i].getAttribute('data-y');
      if(x > min_x & x < max_x & y < max_y & y > min_y){
        arr.push(paths[i]);
      }
    }
    var container = getRandomArr(arr,10);
    container.sort(compare);
    for(var i=0; i<container.length; i++){
      var path = container[i].getElementsByTagName('path')[0];
      path.setAttribute('fill','white');
      path.style.cursor = 'pointer';
      var icon = document.createElementNS("http://www.w3.org/2000/svg","path");
      icon.setAttribute('d',pathArr[i].d);
      icon.setAttribute('fill','#777');
      var size = r/90;
      icon.setAttribute('transform','scale(' + size + ',' + size + ')');
      icon.style.cursor = 'pointer';
      container[i].appendChild(icon);
      container[i].setAttribute('data-url',pathArr[i].url);
      container[i].id = 'link_' + i;
    }
  }
  for(var i=0; i<10; i++){
    (function(n){
      var trigger = document.getElementById('link_' + n);
      trigger.onclick = function(){
        window.location.href = trigger.getAttribute('data-url');
      };
      trigger.onmouseenter = function(){
        trigger.getElementsByTagName('path')[0].setAttribute('fill','#333');
        trigger.getElementsByTagName('path')[1].setAttribute('fill','white');
        var op = 0;
        function setOp(){
          op += 0.1;
          trigger.getElementsByTagName('path')[1].setAttribute('opacity',op);
          if(op < 1){
            setTimeout(setOp,30);
          }
        }
        setOp();
      };
      trigger.onmouseleave = function(){
        trigger.getElementsByTagName('path')[0].setAttribute('fill','white');
        trigger.getElementsByTagName('path')[1].setAttribute('fill','#777');
      };
    })(i);
  }
  function getRandomArr(ori,count){
    var ns = [];
    var len = ori.length;
    for(var i=0; i<count; i++){
      var num = getRandom(len-i);
      ns.push(ori[num]);
      ori.splice(num,1);
    }
    return ns;
  }
  function compare(obj1,obj2){
    var x1 = obj1.getAttribute('data-x');
    var x2 = obj2.getAttribute('data-x');
    var y1 = obj1.getAttribute('data-y')
    var y2 = obj1.getAttribute('data-y')
    if(y1 > y2){
			return 1;
		}else if(y1 < y2){
			return -1;
		}else if(x1 > x2){
			return 1;
		}else if(x1 < x2){
			return -1;
		}else{
			return 0;
		}
  }
  function getRandom(n){
    return Math.floor(Math.random()*n);
  }
}

initPage();
window.onresize = function(){
  initPage();
};
